(()=>{const e=document.querySelector(".menu-toggle");e&&e.addEventListener("click",(()=>{let e=document.getElementById("main-navbar");if(parseInt(getComputedStyle(e).maxHeight)>0)e.style.maxHeight="0px";else{let t=document.querySelector("#main-navbar > ul:first-child");e.style.maxHeight=parseInt(getComputedStyle(t).height)+10+"px"}}))})();